mapper.select:
SELECT M.* FROM ( SELECT ROWNUM AS rowno,N.* from ( SELECT DISTINCT a.company_name,a.credit_code,b.* from CREDIT_COMPANY a left join "BIUSER"."INPUT_BLACKLIST" b on a.credit_code = B.code where 1=1
and b.status in
#{item}
and (b.PUBLISH_by = #{publishBy})
order by b.status,case when GRADE = '高' then 0 when GRADE = '中' then 1 else 3 end,b.UPDATETIME desc )N )M WHERE 1 = 1 AND rowno <= #{pageIndex} * #{pageSize} AND rowno > (#{pageIndex}-1) * #{pageSize}

SELECT COMPANYNAME FROM VIEW_ORG WHERE COMPANYNAME IN ( select COMPANYNAME from VIEW_ORG WHERE 1 = 1
and (SUBCOMPANYNAME = #{CompanyName})
) AND COMPANYNAME= SUBCOMPANYNAME AND TP = '2'

SELECT U.USERNAME FROM CREDIT_USER U WHERE U.ROLE_NAME = '审核专员' AND U.COMPANY_NAME IN (SELECT SUBCOMPANYNAME FROM VIEW_ORG WHERE COMPANYNAME =(SELECT COMPANYNAME FROM VIEW_ORG WHERE COMPANYNAME IN (select COMPANYNAME from VIEW_ORG WHERE 1 = 1
and (SUBCOMPANYNAME = #{approveBy})
) AND COMPANYNAME= SUBCOMPANYNAME AND TP = '2') )

SELECT * FROM (SELECT ROW_NUMBER() OVER(PARTITION BY report_type ORDER BY update_time DESC)rn, t.*FROM CREDIT_REPORT t where 1 = 1
and ( t.credit_code = #{creditCode})
) WHERE rn = 1

SELECT M.* FROM ( SELECT ROWNUM AS rowno,N.* from ( SELECT DISTINCT a.company_name, a.credit_code, b.* FROM CREDIT_COMPANY a LEFT JOIN "BIUSER"."INPUT_BLACKLIST" b ON a.credit_code = B.code WHERE 1 = 1
and b.status in
#{item}
AND b.PID IS NOT NULL AND b.status != 4 AND b.companyname in ( SELECT DISTINCT O.SUBCOMPANYNAME --二级公司 FROM ODS_HR_PSNDOC A LEFT JOIN VIEW_ORG O ON A.ORGCODE=O.SUBCOMPANYCODE WHERE A.CODE=#{userName} AND O.TP IN (1,2) UNION SELECT DISTINCT O.SUBCOMPANYNAME --管理员 FROM VIEW_ORG O WHERE #{userName} = 'admin' AND O.TP IN (1,2) ) ORDER BY b.status, CASE
WHEN GRADE = '高' THEN 0 WHEN GRADE = '中' THEN 1 ELSE 3 END, b.UPDATETIME DESC )N )M WHERE 1 = 1 AND rowno <= #{pageIndex} * #{pageSize} AND rowno > (#{pageIndex}-1) * #{pageSize}

select distinct TYPENAME from ODS_TYC_PUSHSTRUCT P
 LEFT JOIN ODS_TYC_TYPE T ON P.TYPEA=T.TYPE
 WHERE COMPANYNAME in
 (SELECT DISTINCT ENTNAME FROM LOG_TYC_CONCERN WHERE CONCERN_FLAG=1 AND UPDATEBY = #{userName})

       and RISKLEVEL in

           #{item}



       and COMPANYNAME in

           #{item}


 AND TO_CHAR(UPDATETIME,'YYYY-MM-DD')
 BETWEEN #{startDate} AND #{endDate}

