) SELECT COUNT(1) as co FROM ( SELECT * FROM ds1 where 1=1 and TYPENAME in
#{item}
ORDER BY UPDATETIME DESC ) A

SELECT M.* FROM (
SELECT ROWNUM AS rowno,N.*
FROM (
SELECT
PUSH_TYPE,
PROMPTINFO,
USER_NAME,
TO_CHAR(PUSH_TIME,'YYYY-MM-DD') AS UPDATETIME
FROM
BIUSER.INPUT_PUSH
WHERE TO_CHAR(PUSH_TIME,'YYYY-MM-DD') BETWEEN #{startDate} AND #{endDate}
and USER_NAME = #{userName}
and PUSH_TYPE != '企业信息变动'
order by UPDATETIME DESC,PUSH_TYPE DESC
)N
)M
where 1 = 1
AND rowno <= #{pageIndex} * #{pageSize}
AND rowno > (#{pageIndex}-1) * #{pageSize}

SELECT M.* FROM ( SELECT ROWNUM AS rowno,N.* FROM ( SELECT TO_CHAR(R.CREATE_TIME /(1000 * 60 * 60 * 24)+ TO_DATE('1970-01-01', 'YYYY-MM-DD'), 'YYYY-MM-DD') as cd, case R.LEVEL1 when '0' then '一般' when '1' then '重大' when '2' then '警示' end as LEVEL_TYPE, case R.ENT_TYPE when '0' then '非城投企业' when '1' then '城投企业' end as ENT_TYPE, R.DESCRIPTION, R.TYPE_NAME, R.CREDIT_CODE, TO_CHAR(R.UPDATE_TIME /(1000 * 60 * 60 * 24)+ TO_DATE('1970-01-01', 'YYYY-MM-DD'), 'YYYY-MM-DD') as ud, R.ENTERPRISE_NAME FROM BIUSER.ODS_ZCX_RISKEVENT R WHERE TO_CHAR(R.CREATE_TIME /(1000 * 60 * 60 * 24)+ TO_DATE('1970-01-01', 'YYYY-MM-DD'), 'YYYY-MM-DD') BETWEEN #{startDate} AND #{endDate} and ENTERPRISE_NAME in (SELECT DISTINCT ENTNAME FROM LOG_ZCX_CONCERN WHERE CONCERN_FLAG=1 AND UPDATEBY= #{userName})
and R.LEVEL1 in
#{item}
and R.ENTERPRISE_NAME in
#{item}
ORDER BY cd desc,R.ENTERPRISE_NAME )N )M where 1 = 1 AND rowno <= #{pageIndex} * #{pageSize} AND rowno > (#{pageIndex}-1) * #{pageSize}

