FROM BIUSER.ODS_ZCX_NEWSPUBOPINION N WHERE TO_CHAR(N.CREATE_TIME /(1000 * 60 * 60 * 24)+ TO_DATE('1970-01-01', 'YYYY-MM-DD'), 'YYYY-MM-DD') BETWEEN #{startDate} AND #{endDate}
and N.LEVEL1 in
#{item}
